import { Link, useParams } from 'react-router-dom';
import React from 'react';
import games_data from '../../data/games-data';
import '../Game.css'

function SingleGame() {
  const { gameId } = useParams();
  const { controls, name } = games_data.find((game) => game.id === gameId);

  return (
    <>
        {/* <h1>HELLO WORLD</h1> */}
        
        <div className='columnWrapper'>
            <div className='gameView'>
                <iframe title='gameViewIFrame' frameborder="0" src="https://itch.io/embed-upload/6770940?color=ffffff" allowfullscreen="" width="960" height="560"><a href="https://interpied.itch.io/casinocraze-t">Casino Craze</a></iframe>
            </div>
            <div className='sidebar'>
                <Link to='/games'>
                    <p>View All Games</p>
                    </Link>
                <h5>{name ? name : 'game not found'}</h5>
                <p>{controls}</p>
            </div>
        </div>

    </>
  );
}

export default SingleGame;
